import os

def save_object_to_file(obj):
    # Iterate through the items in the object
    for key, value in obj.items():
        # Split the key by '/' to handle multiple folders
        path_parts = key.split('/')

        # Determine the file name as the last part of the path
        file_name = path_parts[-1]

        # Check if there are multiple parts (i.e., directories)
        if len(path_parts) > 1:
            # Determine the parent folder (all parts except the last one)
            parent_folder = os.path.join(*path_parts[:-1])
            # Create the folder structure if it doesn't exist
            os.makedirs(parent_folder, exist_ok=True)
        else:
            # If there is no folder, set the parent_folder to an empty string
            parent_folder = ''

        # Create the full file path
        file_path = os.path.join(parent_folder, file_name)

        # Write the value to the file
        with open(file_path, 'w', encoding='utf-8') as file:
            file.write(str(value))
        print(f"Saved value to {file_path}")


